import "./Carousel.css";
import React, { useState } from "react";

const Carousel = ({ CharactersData }) => {
  const [activeIndex, setActiveIndex] = useState(0);

  const nextSlide = () => {
    setActiveIndex((prevIndex) => (prevIndex + 1) % CharactersData.length);
  };

  const prevSlide = () => {
    setActiveIndex((prevIndex) =>
      prevIndex === 0 ? CharactersData.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="carousel">
      <button className="carousel__btn carousel__btn--prev carousel-btn" onClick={prevSlide}>
        &lt;
      </button>
      <div className="carousel__slide img-">
        <img className="carousel-img" src={CharactersData[activeIndex].img} alt={CharactersData[activeIndex].title} />
      </div>
      <button className="carousel__btn carousel__btn--next carousel-btn" onClick={nextSlide}>
       &gt;
      </button>
    </div>
  );
};


export default Carousel;
