import React from "react";
import { Carousel } from "react-bootstrap";
import CharactersData from "../../groupsData";

const MyCarousel = () => {
  const items = [
    { id: 1, content: "First Slide" },
    { id: 2, content: "Second Slide" },
    { id: 3, content: "Third Slide" },
  ];

  return (
    <Carousel
      interval={5000}
      pause={false} // Set interval and pause to false for continuous looping
      indicators={false} // Hide the indicators if you don't need them
      controls={false} // Hide the controls if you don't need them
    >
      {CharactersData.map((item) => (
        <Carousel.Item key={item.id}>
          <img
            className="d-block w-100" 
            src={item}
            alt={`Slide`}
          />
          <Carousel.Caption>
            <h3>{item.content}</h3>
          </Carousel.Caption>
        </Carousel.Item>
      ))}
    </Carousel>
  );
};

export default MyCarousel;
