import { S} from "../assets/images/slytherin.png";
import G from "../assets/images/gryffindor.jpeg";
import R from "../assets/images/Ravenclaw.avif";
import H from "./assets/images/Hufflepuff.jpg";

export let GroupsData = [
  {
    id: 1,
    groupName: "اسلیترین",
    description:
      "دانش آموزانی که وارد گروه اسلیترین می‌شوند از ویژگی‌های مدبر بودن، جاه‌طلبی،غرور،خودمراقبتی،مصمم بودن و زیرکی برخوردار هستند. ",
    img: S,
    btn: "#1d7452",
  },
  {
    id: 2,
    groupName: "گریفندور",
    description:
      "گروه گریفیندور گروهی است که به دنبال دانش آموزان با ویژگی‌های شجاعت، دلیری، مصمم بودن، جرأت داشتن، قوی دل و جوانمردی می‌گردد. ",
    img: G,
    btn: "#a81945",
  },
  {
    id: 3,
    groupName: "ریونکلاو",
    description:
      "گروه ریونکلا به دنبال دانش آموزان با خلاقیت، علاقه‌مند به یادگیری، هوشمندی، مدارا کننده، زیرکی و دارای دانش بود.",
    img: H,
    btn: "#fee58b",
  },
  {
    id: 4,
    groupName: "هافلپاف",
    description:
      "دانش آموزان با ویژگی‌های عدالت مداری، سخت کوشی، وفاداری، فروتنی، بردباری و منصف بودن به گروه هافلپاف می‌پیوستند. ",
    img: R,
    btn: "#96b5d4",
  },
];
