import CharactersData from "../../groupsData";
import React, { useState } from "react";

function Carousel() {
  let index = 0;

  function showSlide(CharactersData) {
    for (i = 0; i < CharactersData.length; i++) {
      CharactersData[i].style.display = "none";
      // Circles[i].className = Circles[i].className.replace(" white", "orange");
    }
    
    return(CharactersData[index].style.display = "block");
  }

  function nextSlide() {
    ++index;
    if (index > CharactersData.length - 1) {
      index = 0;
    }
    showSlide(CharactersData);
  } 
  function prevSlide() {
    --index;
    if (index < 0) {
      index = CharactersData.length - 1;
    }
    showSlide(CharactersData);
  }
  setInterval(nextSlide, 5000);

  function nextBtn() {
    nextSlide();
  }

  function prevBtn() {
    prevSlide();
  }

  return (
    <div className="carousel">
      <button className="carousel__btn carousel__btn--prev" onClick={prevBtn}>
        &lt;
      </button>
      <button className="carousel__btn carousel__btn--next" onClick={nextBtn}>
        &gt;
      </button>
    </div>
  )

}

export default Carousel
