import {Col, Row } from "react-bootstrap";
import Hogarts from "../../assets/images/Hogwartscrest.webp";
import "./Footer.css";
function Footer() {
  return (
    <footer>
  
        <Row
          className="fullWidth"
        >
          <Col>
            <h2 className="h2">هری پاتر و هاگوارتز</h2>
          </Col>
          <Col className="photo">
            <img src={Hogarts} className="p-3 float-left" />
          </Col>
        </Row>
      
    </footer>
  );
}

export default Footer;
