import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import S from '../../assets/';
import G from "../../assets/images/gryffindor.jpeg";
import R from "../../assets/images/Ravenclaw.avif";
import H from "../../assets/images/Hufflepuff.jpg";
import { Link } from "react-router-dom";
import './Cards.css'

function Cards({ groupName, description, img, btn }) {
  return (
    <Card
      style={{
        padding: "1rem",
        display: "flex",
        flexDirection: "column",
        gap: "1rem",
        alignItems: "center",
        minHeight: "10rem",
      }}
    >
      <Card.Img
        variant="top"
        style={{ height: "10rem", width: "10rem", textAlign: "center" }}
        src={img}
      />
      <Card.Body style={{ height: "15rem" }}>
        <Card.Title>{groupName}</Card.Title>
        <Card.Text style={{ textAlign: "justify" }}>{description}</Card.Text>
        <Button
          style={{ textAlign: "center", width: "100%", backgroundColor: btn }}
          className="hover"
        >
          <Link className="button">اطلاعات بیشتر</Link>
        </Button>
      </Card.Body>
    </Card>
  );
}

export default Cards;
