import React, { useState } from "react";

const Carousel = ({ CharactersData }) => {
  const [activeIndex, setActiveIndex] = useState(0);

  const nextSlide = () => {
    setActiveIndex((prevIndex) => (prevIndex + 1) % CharactersData.length);
  };

  const prevSlide = () => {
    setActiveIndex((prevIndex) =>
      prevIndex == 0 ? CharactersData.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="carousel">
      <div className="carousel__slide">
        <img
          src={CharactersData[activeIndex].image}
          alt={CharactersData[activeIndex].title}
        />
      </div>
      <button className="carousel__btn carousel__btn--prev" onClick={prevSlide}>
        &lt;
      </button>
      <button
        className="carousel__btn carousel__btn--next"
        onClick={nextSlide}
      ></button>
    </div>
  );
};

export default Carousel;
